angular.module('noteSth', [
    'ngRoute',
    'LocalStorageModule',
    'angularCharts'
])
.config(function($routeProvider, $locationProvider) {
    $routeProvider
        .when('/login', {
            templateUrl: '/views/login.html',
            controller: 'login'
        })
        .when('/register', {
            templateUrl: '/views/register.html',
            controller: 'register'
        })
        .when('/dash', {
            templateUrl: '/views/dash.html',
            controller: 'dashboard'
        })
        .when('/', {
            templateUrl: '/views/index.html',
            controller: 'home'
        })
        .otherwise({
            redirectTo: '/'
        });

    $locationProvider.html5Mode({
        enabled: true,
        requireBase: false
    });
})

var app = angular.module('noteSth');
app.controller('base', ['$scope', 'dataS', 'localStorageService', '$location',

    function($scope, dataS, localStorageService, $location) {
       var token = localStorageService.get('token');
        if(token && token.length > 2) {
           dataS.getData('/user', token)
           .success(function(data){
               $scope.usr = data;
           })
       }
       $scope.callmodal = function(type) {
           $scope.modal = type;
       }
    }
]);

angular.module('noteSth').service('config', function ( ) {
    return {
        API : "http://localhost:4000/api",
        IMG : "http://localhost:4000/img"

    }
});

angular.module('noteSth').service('dataS',  function ($http, config, localStorageService) {
    var headers = function(token) {
        if(token) {
            return {
                'Content-type': 'application/json',
                'x-access-token': localStorageService.get('token')
            }
        } else {
            return {
                'Content-type': 'application/json'
            }
        }
    }
    return {
        getData: function (url, token) {
            return $http({
                method: 'GET',
                url: config.API + url,
                headers: headers(token)
            });
        },
        postData: function (url, data, token) {
            return $http({
                method: 'POST',
                url: config.API + url,
                data: data,
                headers: headers(token)
            });
        },
        editData: function (url, data, token) {
            return $http({
                method: 'PUT',
                url: config.API + url,
                data: data,
                headers: headers(token)
            });
        },
        delData: function (url, hash, token) {
            return $http({
                method: 'DELETE',
                data: {
                    hash: hash
                },
                url: config.API + url,
                headers: headers(token)
            });
        }
    };
});

var app = angular.module('noteSth');
app.controller('dashboard', ['$scope', 'dataS', 'localStorageService', '$location', '$rootScope', 'config',

  function($scope, dataS, localStorageService, $location, $rootScope, config) {
    dataS.getData('/websites', true)
      .success(function(data) {
        $scope.websites = data;
        for (var i = 0, len = $scope.websites.length; i < len; i++) {
            $scope.responses[$scope.websites[i].website] = {};
            $scope.responses[$scope.websites[i].website]['data'] = [];
            $scope.responses[$scope.websites[i].website]['series'] = [''];
          getXRecords(20, $scope.websites[i]);
          new Status($scope.websites[i])
        }
      })
    $scope.statuses = [];
    $scope.responses = {};
    var indexes = 0;

    function Status(website) {
      $scope.statuses = [];
      dataS.getData('/status/' + website._id, true)
        .success(function(data) {
          if (data && data.hasOwnProperty('website')) {
            $scope.statuses.push(data);
            $scope.responses[data.website]['data'].push({
              x: indexes,
              y: [data.response]
            });
            indexes++;

            if ($scope.responses[data.website]['data'].length >= 25) {
              $scope.responses[data.website]['data'].splice(0, 1);

            }
          }
        })

      setTimeout(function() {
        Status(website)
      }, website.timeout * 60000);
    }
    function getXRecords(nmb, website) {
        dataS.getData('/xstatus/' + website._id +'/'+ nmb , true)
        .success(function(data){
          for (var i = 0, len = data.length; i < len; i++) {
            if (data[i] && data[i].hasOwnProperty('website')) {
              $scope.responses[data[i]['website']]['data'].push({
                x: indexes,
                y: [data[i].response]
              });
              indexes++;
            }
          }

        })
    }

    $scope.getStat = function(ele) {
      for (var i = 0, len = $scope.statuses.length; i < len; i++) {
        if ($scope.statuses[i] !== null && $scope.statuses[i].website == ele) {
          return {
            status: $scope.statuses[i].status,
            response: $scope.statuses[i].response
          };
        }
      }
      return null;
    }
    $scope.server = config.IMG + '/';

  }
]);

var app = angular.module('noteSth');
app.controller('home', ['$scope', 'dataS', 'localStorageService', '$location',

    function($scope, dataS, localStorageService, $location) {
       if(localStorageService.get('token').length > 2) {
           $location.path('/dash');
       }
       
    }
]);   
angular.module('noteSth').directive('modal', function() {
   return {
       restrict: 'E',
       link: function(scope, element, attrs) {
           scope.getContentUrl = function() {
               if(attrs.type) {
                return '/views/modals/' + attrs.type + '.html';
                   } else {
                       return ;
                   }
           }
       },
       template: '<div class="note-modal" ng-include="getContentUrl()"></div>'
   }
});
var app = angular.module('noteSth');
app.controller('websites', ['$scope', 'dataS', 'localStorageService', '$location', '$rootScope',

    function($scope, dataS, localStorageService, $location, $rootScope) {
       $scope.website = {
           body: {},
           send: function() {
               dataS.postData('/websites', $scope.website.body, true)
               .success(function(data) {
                   $rootScope.$emit('noteAdd', data);
               })
           }
       }

    }
]);

var app = angular.module('noteSth');
app.controller('login', ['$scope', 'dataS', 'localStorageService', '$location',

    function($scope, dataS, localStorageService, $location) {

        $scope.logindo = function() {
                dataS.postData('/auth', $scope.login)
                .success(function(data) {

                    localStorageService.set('token', data.token);
                    if(data.token) {
                         $location.path('/dash');
                    }
                })

        }
    }
]);

var app = angular.module('noteSth');
app.controller('register', ['$scope', 'dataS',

    function($scope, dataS) {
       
        $scope.registerdo = function() {
                dataS.postData('http://localhost:4000/api/user', $scope.login)
                .success(function(data) {
                    console.log(data);
                })
                
        }
    }
]);  